changeSet.sql:
INSERT INTO roles(id, name)
     SELECT gen_random_uuid(), 'ROLE_ADMIN'
      WHERE NOT EXISTS (
         SELECT * FROM roles WHERE name = 'ROLE_ADMIN'
      );
INSERT INTO roles(id, name)
     SELECT gen_random_uuid(), 'ROLE_USER'
      WHERE NOT EXISTS (
          SELECT * FROM roles WHERE name = 'ROLE_USER'
      )
